import React from "react";
import io from "socket.io-client";
import Home from "./components/Home";
import Chat from "./components/Chat";
import { Routes, Route, Navigate } from 'react-router-dom'

const socket = io.connect("http://localhost:3001");

function App() {
  const joinRoom = (name, room) => {
    socket.emit("joinRoom", { username: name, roomname: room });
  };

  return (
    <div>
      <Routes>
        <Route path="/" element={<Home joinRoom={joinRoom} />} />
        <Route
          path="/chat/:roomname/:username"
          element={(props) => <Chat socket={socket} {...props} />}
        />
        {/* Redirect to Home if URL doesn't match */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </div>
  );
}

export default App;
